import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

  _userName: string = '';
  loginEmail: string = '';
  loginPassword: string = '';
  registerEmail: string = '';
  registerPassword: string = '';

  login(): void {
    // Aquí implementarías la lógica de inicio de sesión.
    // Por ejemplo, verificar si las credenciales son válidas.
    if (this.loginEmail === 'user@example.com' && this.loginPassword === 'password') {
      this._userName = 'User'; // Usuario autenticado
    }
  }

  logout(): void {
    this._userName = '';
  }

  register(): void {
    // Aquí implementarías la lógica de registro.
    // Por ejemplo, crear una nueva cuenta de usuario.
    // Debes realizar validaciones adecuadas y conectarte a un servicio backend para almacenar los datos del usuario.
  }
}
<div class="login-container">
  <h2>Login</h2>
  <form (ngSubmit)="login()" *ngIf="!_userName">
    <div class="form-group">
      <label for="loginEmail">Email</label>
      <input type="email" id="loginEmail" [(ngModel)]="loginEmail" name="loginEmail" class="form-control" required>
    </div>
    <div class="form-group">
      <label for="loginPassword">Password</label>
      <input type="password" id="loginPassword" [(ngModel)]="loginPassword" name="loginPassword" class="form-control" required>
    </div>
    <button type="submit" class="btn btn-primary">Login</button>
  </form>

  <h2 *ngIf="_userName">Welcome, {{ _userName }}</h2>
  <button (click)="logout()" *ngIf="_userName" class="btn btn-secondary">Logout</button>

  <h2>Register</h2>
  <form (ngSubmit)="register()" *ngIf="!_userName">
    <div class="form-group">
      <label for="registerEmail">Email</label>
      <input type="email" id="registerEmail" [(ngModel)]="registerEmail" name="registerEmail" class="form-control" required>
    </div>
    <div class="form-group">
      <label for="registerPassword">Password</label>
      <input type="password" id="registerPassword" [(ngModel)]="registerPassword" name="registerPassword" class="form-control" required>
    </div>
    <button type="submit" class="btn btn-primary">Register</button>
  </form>
</div>
