import {Component, OnInit, ViewEncapsulation} from '@angular/core';
import {ActivatedRoute} from '@angular/router';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css'],
  encapsulation: ViewEncapsulation.None
})
export class HomeComponent implements OnInit {

  constructor(
    private route: ActivatedRoute) {
  }

  needsLogin: boolean | undefined;
  _userName: string = '';

  ngOnInit() {
    this.needsLogin = !!this.route.snapshot.params['needsLogin'];
  }

  get userName(): string {
    return this._userName;
  }

  login(): void {
    this._userName = 'Amilcar Romero';
  }

  logout(): void {
    this._userName = '';
  }


}
<!DOCTYPE html>
<html>
<head>
    <link rel="stylesheet" type="text/css" href="home.component.css">
</head>
<body>
    <div class="card">
        <div class="header">
            <h1 class="title"> BIENVENIDO {{userName}}</h1>
        </div>
        <div class="content">

            <div *ngIf="needsLogin" class="alert alert-warning">
                You have been redirected to this page because you don't have the necessary
                rights for the requested action. Login with a respective user account!
            </div>

            <button (click)="login()" class="btn btn-default">Login</button>
            <button (click)="logout()" class="btn btn-default">Logout</button>

            <!-- Agrega aquí el tabbed-pane si lo necesitas -->
        </div>
    </div>
</body>
</html>
