import { Component, OnInit } from '@angular/core';
import { ActivatedRoute } from '@angular/router';
import { Router } from '@angular/router';  // Importación del módulo de enrutamiento de Angular
import { FlightService } from '../flight.service';
import { Flight } from '../flight';
import { map, switchMap } from 'rxjs/operators';
import { of } from 'rxjs';

@Component({
  selector: 'app-flight-edit',
  templateUrl: './flight-edit.component.html'
})
export class FlightEditComponent implements OnInit {

  id!: string;  // Variable para almacenar el ID del vuelo
  flight!: Flight;  // Variable para almacenar los datos del vuelo
  feedback: any = {};  // Variable para retroalimentación y mensajes al usuario

  constructor(
    private route: ActivatedRoute,  // Inyección del servicio ActivatedRoute para acceder a los parámetros de la URL
    private router: Router,  // Inyección del servicio Router para la navegación
    private flightService: FlightService  // Inyección del servicio FlightService para gestionar los vuelos
  ) {
  }

  ngOnInit() {
    // Observa los cambios en los parámetros de la URL y realiza acciones en consecuencia
    this
      .route
      .params
      .pipe(
        map(p => p.id),  // Extrae el ID del parámetro en la URL
        switchMap(id => {
          if (id === 'new') { return of(new Flight()); }  // Si el ID es 'new', crea un nuevo objeto Flight
          return this.flightService.findById(id);  // En caso contrario, busca un vuelo por su ID utilizando el servicio
        })
      )
      .subscribe(flight => {
          this.flight = flight;  // Asigna los datos del vuelo recuperado
          this.feedback = {};  // Limpia los mensajes de retroalimentación
        },
        err => {
          this.feedback = {type: 'warning', message: 'Error loading'};  // En caso de error, muestra un mensaje de advertencia
        }
      );
  }

  save() {
    // Guarda los datos del vuelo utilizando el servicio FlightService
    this.flightService.save(this.flight).subscribe(
      flight => {
        this.flight = flight;  // Actualiza los datos del vuelo con los cambios guardados
        this.feedback = {type: 'success', message: 'Save was successful!'};  // Muestra un mensaje de éxito
        setTimeout(() => {
          this.router.navigate(['/flights']);  // Navega de nuevo a la lista de vuelos después de un segundo
        }, 1000);
      },
      err => {
        this.feedback = {type: 'warning', message: 'Error saving'};  // En caso de error, muestra un mensaje de advertencia
      }
    );
  }

  cancel() {
    this.router.navigate(['/flights']);  // Cancela la edición y vuelve a la lista de vuelos
  }
}
