    padding-left: 10px;
    font-family: 'ABeeZee', sans-serif;
    background: none;
    text-align: center; 

  }

  .label{
    position: absolute;
    top: 0;
    left:0;
    font-size: 14px;
    color:rgb(10, 32, 54) ;
