import { Component } from '@angular/core';

@Component({
  selector: 'app-home',
  templateUrl: './home.component.html',
  styleUrls: ['./home.component.css']
})
export class HomeComponent {

  _userName: string = '';
  loginEmail: string = '';
  loginPassword: string = '';
  registerEmail: string = '';
  registerPassword: string = '';

  login(): void {
    // Aquí implementarías la lógica de inicio de sesión.
    // Por ejemplo, verificar si las credenciales son válidas.
    if (this.loginEmail === 'user@example.com' && this.loginPassword === 'password') {
      this._userName = 'User'; // Usuario autenticado
    }
  }

  logout(): void {
    this._userName = '';
  }

  register(): void {
    // Aquí implementarías la lógica de registro.
    // Por ejemplo, crear una nueva cuenta de usuario.
    // Debes realizar validaciones adecuadas y conectarte a un servicio backend para almacenar los datos del usuario.
  }
}
<div class="card">
  <div class="header">
    <h1 class="title"> BIENVENIDO {{userName}}</h1>
  </div>
  <div class="content">

    <div *ngIf="needsLogin" class="alert alert-warning">
      You have been redirected to this page because you don't have the necessary
      rights for the reqeusted action. Login with a respective user account!
    </div>

    <button (click)="login()" class="btn btn-default">Login</button>
    <button (click)="logout()" class="btn btn-default">Logout</button>

    
